INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('abydos', '阿比多斯高中', null, null, null, '阿比多斯高中曾经面积广阔、人口众多，但随着来源不明的沙尘暴在区域内席卷，大部分土地都变成了沙漠，学生会为了阻止沙漠化蔓延却毫无成效，濒临破产废校，居民也逐渐迁出，繁华之地走向荒芜。目前仅剩废校对策委员会的五位成员还留在此地。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_ABYDOS.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('arianism', '阿里乌斯分校', null, null, null, '在推动圣三一综合学园完成联合统一的「第一次公会议」中，由于阿里乌斯派系直到最后都持反对意见，最后与统一后的圣三一发生了争斗，遭到镇压，被逐出了圣三一的自治区，隐藏在了一个完全没人能找到的地方，逐渐被世人所遗忘……', 'https://static.kivo.wiki/images/gallery/28/School_Icon_Arius.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('du', '联邦学生会', null, null, null, '联邦学生会即是全基沃托斯学院的管理局、又起着联邦政府一样的责任。主要部门分为统括部与行政委员会，行政会委员会中十一个部门又分辖着整个基沃托斯各种各样的工作。', 'https://static.kivo.wiki/images/gallery/28/%E8%81%94%E9%82%A6%E5%AD%A6%E7%94%9F%E4%BC%9A.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('gehena', '格赫娜学园', null, null, null, '基沃托斯三大学院之一，其学生会名为“万魔殿”。用两个词来概括这里的风气，就是“自由”和“混沌”，每天都有人惹是生非，让负责维持秩序的风纪委员会非常头疼。当然，拜这些随心所欲、行为出格的家伙们所赐，格赫娜的风纪委员也锻炼出了强大的实力。和圣三一的关系相当差，但最近似乎也有少许缓和的倾向。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_GEHENNA.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('helland', '海兰德铁道学园', null, null, null, '海兰德铁道学园是一所同时有着铁路运营和进行相关教育机能的独立学园。尽管海兰德并不拥有自己的自治区，但是海兰德实际控制的铁路及其沿线设施在职能上充当了海兰德的自治区。在管理着基沃托斯内部分铁路的同时，还有着其他多项业务。', 'https://static.kivo.wiki/images/gallery/28/%E9%93%81%E9%81%93.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('kronos', '克罗诺斯学校', null, null, null, '以新闻记者为主的学校，活跃于剧情中的各种报道环节。', 'https://static.kivo.wiki/images/gallery/28/%E5%85%8B%E7%BD%97%E8%AF%BA%E6%96%AF.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('millennium', '千禧科学学院', null, null, null, '基沃托斯三大学院之一，千禧即Millennium，千禧年的简称。其学生会名为“研讨会”，由女仆组成的C&C负责维持秩序，工程部也名声在外。正如名字所示，干禧最关注技术，基沃托斯的前沿发明几乎都出自他们之手。学生以理科生居多，不乏醉心学术对现实生活反而不太关心的怪异天才。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_MILLENNIUM.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('red_winter', '红冬联邦学园', null, null, null, '基沃托斯占地面积最大的学院，其理事会名为“红冬事务局”。因其地处偏僻，联邦理事会的权威很难辐射到此地，所以自治程度要高于一般学院。红冬可以说是“反逆”的代名词，理事会长经常被推翻，街道上也总能看到抗议人群，学员们对此习以为常，并不影响日常生活。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_REDWINTER.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('schale', '夏莱', null, null, null, '「夏莱（Schale/シャーレ）」是由联邦学生会长成立的社团——联邦搜查部，与单纯的社团活动不同，夏莱是一种超越法规的机构。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_ETC.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('shan_hai_jing', '山海经高级中学', null, null, null, '以中华风为特色的学院，有梅花园、炼丹研究会等社团。与基沃托斯的其它学院相比，文化十分独特，并且有着悠久的历史传统。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_SHANHAIJING.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('srt', 'SRT特殊学园', null, null, null, 'SRT特殊学园是由联邦学生会长亲自创立的特殊部队，是在联邦学生会长的许可之下，为了治理瓦尔基里警察学校和各自治区风纪委员力不能及的D.U地区内部犯罪而设立的。拥有基沃托斯最先进的武器装备和最精英的团队。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_SRT.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('trinity', '圣三一综合学园', null, null, null, '基沃托斯三大学院之一，其学生会名为“茶话会”。设有自警团、正义实现委员会、救护骑士团等许多社团。总体来说重视纪律和文化，学生们大多诚实善良，给人不谙世事的大小姐印象，内部的治安状况有序而稳定。自古以来就在基沃托斯推崇“文武双全”，是一所充满历史和传统气息的学园。[1-Vol3.1.3 茶话会]当然，圣三一并不是完美的。在风平浪静的学院氛围下，也潜藏着诸多问题……和格赫娜在传统上是敌对关系。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_TRINITY.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('valkyrie', '瓦尔基里警察学校', null, null, null, '瓦尔基里警察学校是有着处理基沃托斯各种各样问题职能的学校。因此在各处都有据点，并且学生大多富有正义感，但即使这样因为各自治区的限制瓦尔基里几乎解决不了什么问题…', 'https://static.kivo.wiki/images/gallery/28/School_Icon_VALKYRIE.png', null, null);
INSERT INTO schools (id, cn_name, jp_name, kr_name, en_name, introduce, logo, beautify_logo, bg) VALUES ('yamato', '百鬼夜行联合学园', null, null, null, '以和风为特色，旅游业发达，有庆典运营管理部、忍术研究部、修行部、阴阳部等风格独特的社团。百鬼夜行联合学园并没有正式的学生会，而是由众多彼此独立的社团、委员会联合组成的。', 'https://static.kivo.wiki/images/gallery/28/School_Icon_HYAKKIYAKO.png', null, null);
